local EquipmentController = class("EquipmentController",Controller)

function EquipmentController:init()
	
end


function EquipmentController:equipment_openForge(_,data)

		
	local view = ViewManager.getView("EquipmentView")
	if view == nil then
		view = ViewManager.open("EquipmentView",{eqtype = 2})
	else
		view.viewCtrl:setSelectedIndex(2)
	end
	
end

function EquipmentController:pack_equip_change(_,eq)
	if not eq then return end 
	local data = eq[1].itemData.__data
	local eqData = data.specialData and data.specialData.equipment or {};
	local info = {
		code = data.code,
		id = data.id,
		uuid = data.uuid,
	}
	for k, v in pairs(eqData) do
		info[k] = v;
	end
	EquipmentModel:setSkillData(info.uuid, info);
end

return EquipmentController