local ExtraordinarylevelPvPController = class("ExtraordinarylevelPvP", Controller)

function ExtraordinarylevelPvPController:init()
end

--#阶段积分状态发生 通知
function ExtraordinarylevelPvPController:CrossSuperMundane_DanScoreNotifyInfo(_, data)
    printTable(159, "阶段积分状态发生 通知", data)
    for key, value in pairs(data) do
        ExtraordinarylevelPvPModel.ExtraordinarylevelMainInfo[key]=value
    end
    RedManager.updateValue("M_EXTRAORECORDRED", true)
end

--#系统赛季信息 登录下发
function ExtraordinarylevelPvPController:CrossSuperMundane_SeasonNotifyInfo(_, data)
    printTable(159, "系统赛季信息 登录下发", data)
    if  data and data.seasonInfo  then
        ExtraordinarylevelPvPModel:setSeasonNotify(data.seasonInfo) 
    end
end

--##购买王者之征
function ExtraordinarylevelPvPController:CrossSuperMundane_NotifyBuyKingJourneyInfo(_, data)
    printTable(159, "购买王者之征", data)
    ExtraordinarylevelPvPModel:setWangzhezhizheng(data)
    Dispatcher.dispatchEvent(EventType.extraordinarylevelPvP_wangzhezhizheng)
end

return ExtraordinarylevelPvPController
