local FashionController = class("FashionController",Controller)
local FashionConfiger = require "Game.ConfigReaders.FashionConfiger"

function FashionController:init()
	
end

function FashionController:FashionLoginTips_Show()
	local info = FashionConfiger.getFashionActivityConfiger()
	if info then 
		local openDay = ServerTimeModel:getOpenDay() + 1
		local isHave = ModelManager.PackModel:getFashionBag():getIsHaveFashion(info.fashionId)
		if (info.time and openDay >= info.time) and not isHave then 
			local jump = info.jump[1]
			local jumpId = false
			local isOpen = false
			if jump.jumpType == 1 then --功能
				isOpen = not ModuleUtil.getModuleOpenTips(jump.jumpId)
				jumpId = jump.jumpId
			elseif jump.jumpType == 2 then --活动
				local activityData = ModelManager.ActivityModel:getActityByType(jump.jumpId)
				isOpen = activityData
				jumpId = activityData and activityData.showContent.moduleOpen
			end
			if isOpen then 
				ViewManager.open("FashionLoginTipsView",{fashionActivityInfo = info,jump = jumpId})
			end
		end
	end
end



return FashionController