local FashionModel = class("FashionModel", BaseModel)
local FashionConfiger = require "Game.ConfigReaders.FashionConfiger"

function FashionModel:ctor()
    self.isOpen = false
end

-- 获取某个精灵的所有皮肤
function FashionModel:getFashionList(heroId)
	local fashionList = {}
    local fashionInfo = FashionConfiger.getAllFashionInfoByHeroId(heroId)
    local num = #fashionInfo + 2
    for i=1,num do
        if i == 1  then 
            fashionInfo[1].index = 1
    		table.insert(fashionList,fashionInfo[1]) --开头第一个（为了滑动效果）
        elseif i == num then 
            fashionInfo[num-2].index = num
    		table.insert(fashionList,fashionInfo[num-2]) --结尾最后一个（为了滑动效果）
        else
            fashionInfo[i-1].index = i-1
    		table.insert(fashionList,fashionInfo[i-1]) --真正的内容
    	end
    end
    local keys ={
        -- {key="sort",asc = false},
        {key="index",asc = false},
    }
    TableUtil.sortByMap(fashionList,keys)
    return fashionList 
end

function FashionModel:setIsOpen(bool)
    self.isOpen = bool
end

function FashionModel:getIsOpen()
    return self.isOpen
end

return FashionModel
