local HeroBossActivityModel = class("HeroBossActivityModel", BaseModel)

function HeroBossActivityModel:ctor()
	self.moduleId = 1
end

function HeroBossActivityModel:init()

end
function HeroBossActivityModel:getActShopData()
	local actData = ModelManager.ActivityModel:getActityByType(GameDef.ActivityType.NewHeroShop)
	return actData
end
function HeroBossActivityModel:getActData()
	local actData = ModelManager.ActivityModel:getActityByType(GameDef.ActivityType.NewHeroCopy)
	return actData
end
function HeroBossActivityModel:getModuleId()
	local actData = ModelManager.ActivityModel:getActityByType(GameDef.ActivityType.NewHeroCopy)
	self.moduleId = actData and actData.showContent.moduleId or 1
	return self.moduleId
end
function HeroBossActivityModel:getBoxNum(mark)
	local num = 0
	local config = DynamicConfigData.t_NewHeroBossReward[self.moduleId] or {}
	for key,value in pairs(config) do
		if mark > value.min and mark < value.max then
			num = value.id
		end
	end
	return num
end
return HeroBossActivityModel
